const ADD = 'ADD';
// const DEC = 'DEC';

const initialValues = {
  input1:0,
  input2:0,
  sum:0,
  flag:true
}

export default (state = initialValues, action) => {
  switch (action.type) {
    case ADD : 
      return { sum:state.input1 + state.input2 };
    case "val":
        if(action.value===true)
        {
            return{...state,flag:true}
        }
        else
        {
            return{...state,flag:false}

        }
    default :
      return state;
  };
};

export const values=(item)=>
{
  return{
      type:"val",
      value:item
  }
}
export const Addition= () => {
  return {
    type:ADD
  }
};

