const ADD = 'ADD';
// const DEC = 'DEC';

const initialValues = {
  input1:0,
  input2:0,
  sum:0
}

export default (state = initialValues, action) => {
  switch (action.type) {
    case ADD : 
      return { sum:state.input1 + state.input2 };
    default :
      return state;
  };
};

export const Input1 = ({handleChange,value}) =>{
    return(
    <div>
      <input type="text" value={value} onChange={(event) => handleChange(event.target.value)} />
