import React from 'react';
const ADD = 'ADD';
// const DEC = 'DEC';

const initialValues = {
  input1:0,
  input2:0,
  sum:0
}

export default (state = initialValues, action) => {
  switch (action.type) {
    case ADD : 
      return { sum:state.input1 + state.input2 };
    default :
      return state;
  };
};



export const Addition= () => {
  return {
    type:ADD
  }
};

