import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import {Addition} from './reducers/counter';
import './App.css';

// contributor - Sunil PutteGowda (hp.sunilkumar793@gmail.com)
const App = ({ input1,input2,sum, ...props }) => {
  return (
    <div className="App">
    const Input1 = ({handleChange,value}) => {
      <div>
        <input type="text" value={input1} onChange={(event) => handleChange(event.target.value)} />
      </div>
      };

     const Input2 = ({handleChange,value}) => {
      <div>
        <input type="text" value={input2} onChange={(event) => handleChange(event.target.value)} />
      </div>
     };

      <button onClick={() => props.Addition()}>
        +
      </button>
        <h1>
        {sum}
        </h1>
      {/* <button onClick={() => props.decrement()}>
        -
      </button> */}
    </div>
  );
};

const mapStateToProps = ({ counter }) => ({
  sum: counter.sum
});

const mapDispatchPropsToProps = (dispatch) => bindActionCreators({
  Addition
}, dispatch);

export default connect(mapStateToProps, mapDispatchPropsToProps)(App);
